    public GeminiResponseDTO SolicitacaoIA(Chamado c, String api_key) throws ResponseStatusException {
//      Retorna um Json que contém a resposta do HttpRequest da Gemini API
        try {
            String s = comunicarGemini(c, api_key);
            return JsonToObject.JsonToResponse(s);
        } catch (Exception e) {
            throw new ResponseStatusException(
                    HttpStatus.FORBIDDEN, "API key não válida"
            );
        }
    }

    public HashMap<String, Chamado> mapearChamadosESetaIndice() throws FileNotFoundException {
        // Caminho para o arquivo chamados.json
        String caminhoArquivoJson = "src/main/resources/chamados/chamados.json";

        // Leitura do arquivo JSON
        Gson gson = new GsonBuilder().create();
        // Salva o tipo da List para o campo do método fromJson
        Type listType = new TypeToken<List<Chamado>>() {
        }.getType();
        // Insere os chamados do Arquivo JSON para a List
        List<Chamado> chamados = gson.fromJson(new FileReader(caminhoArquivoJson), listType);

        //HashMap para acessar os dados pela Primary Key ao invés do índice da List
        HashMap<String, Chamado> indiceChamados = new HashMap<>();
        for (Chamado chamado : chamados) {
            indiceChamados.put(chamado.getNumero(), chamado);
        }


        return indiceChamados;
    }


